local ix=ix local a=math.max local b=math.random local c=timer.Exists local d=timer.Adjust local e=timer.Create local CAMI=CAMI local f=net.Start local g=SERVER and net.Send local assert=assert local istable=istable local h=player.GetHumans local pairs=pairs local i=game.MaxPlayers local j=ix.plugin.list["artefacts-system"]j.name="Artefacts System"j.author="PURP"j.description=""ix.artefacts=ix.artefacts or{}CAMI.RegisterPrivilege({Name="Helix - Artefacts System",MinAccess="superadmin",})function ix.artefacts.HasAccess(k)if not CAMI.PlayerHasAccess(k,"Helix - Artefacts System")then k:Notify("У вас недостаточно прав.")return false end if(k._nextArtefactsAction or 0)>CurTime()then k:Notify("Нужно подождать слишком частые запросы.")return false end k._nextArtefactsAction=CurTime()+2 return true end ix.command.Add("ArtefactsAdminMenu",{description="Система артефактов - Админ меню",privilege="Artefacts System",superAdminOnly=true,OnRun=function(self,k)net.Start("artefactsOpenAdminMenu")net.WriteTable(ix.artefacts.GetData())net.Send(k)end})ix.util.Include("sv_plugin.lua")ix.util.Include("cl_plugin.lua")ix.lang.AddTable("russian",{optArtefacts_respawn_min="Респавн MIN в минутах",optArtefacts_respawn_max="Респавн MAX в минутах",})ix.config.Add("artefacts_respawn_min",60,"Мин время через которое появиться артефакты в локации в мин",function(k,l)if SERVER then local m=ix.artefacts.GetData()for n,o in next,m do ix.artefacts.InitializeTimer(n,o)end print("ARTEFACTS TIMERS Initialize")end end,{data={min=1,max=360},category="Artefacts",})ix.config.Add("artefacts_respawn_max",60,"Макс время через которое появиться артефакты в локации в мин",function(k,l)if SERVER then local m=ix.artefacts.GetData()for n,o in next,m do ix.artefacts.InitializeTimer(n,o)end print("ARTEFACTS TIMERS Initialize")end end,{data={min=1,max=360},category="Artefacts",})