local a=ix.plugin.list["inventory-module-aitems-list"]local b=ix.itemsListMaterials ix.itemsList.data.helmet={["_slot"]=true,["_Loadout"]=true,["_Tooltip"]=function(c,d)a:TooltipProtectStats(c,d,panelBase)a:TooltipRenderTable(c,d)a:TooltipPlayerModel(c,d,panelBase)end,["actors_helmet_01"]={name="Берет",description="Головной убор, распространенный среди военнослужащих по всему миру. Используется как отличительный знак, часто содержащий символику той или иной военной структуры. В Зоне используется в основном офицерами охраны Периметра и высокопоставленными членами сталкерских группировок.",durabilityQuality=3.5,durabilityPrice=480,weight=0.1,actorsTier=1,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_01.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_01.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[6]=true,[7]=true,},},itemIcon={material=b.wick_icon_equipment_seva,x=3500,y=2150,width=100,height=100,},},["actors_helmet_02"]={name="Бандана",description="Обыкновенная бандана из легкой ткани поможет защитить голову от ветра и солнца, но не предоставит никакой действенной защиты от иных опасностей Зоны.",durabilityQuality=3.5,durabilityPrice=600,weight=0.1,actorsTier=2,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/helmet_02_outfit.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=0,width=100,height=100,},},["actors_helmet_02a"]={name="Вязаная шапка",description="Вязаная шапка. Как гласит сохранившаяся этикетка, изготовлена из синтетической основы с добавлением натуральной шерсти. Популярна среди сталкеров за дешевизну и возможность не застудить голову.",actorsNoPaint=true,weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=5,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02a.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02a.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=100,width=100,height=100,},},["actors_helmet_03"]={name="Шапка-ушанка",description="Распространенный зимний головной убор в странах постсоветского пространства. В Зоне же используется сталкерами для защиты от плохой погоды. Из иных плюсов можно выделить плотную ткань, которая может защитить голову от ударов некрупными предметами.",actorsNoPaint=true,weight=0.45,actorsTier=3,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=8,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_03.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_03.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={},itemIcon={material=b.ui_icon_armorlist_mz,x=3200,y=0,width=100,height=100,},},["actors_helmet_01a"]={name="Жиганка",description="Популярная кепка среди представителей и приближенных криминального мира времен 90-х. Став культовой особенностью авторитетов, сразу же завоевала особое место в образе местных бандитов",durabilityQuality=3.5,durabilityPrice=720,weight=0.1,actorsTier=1,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_01a.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_01a.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3000,y=100,width=100,height=100,},},["actors_helmet_02b"]={name="Лыжная шапка",description="Вязаная шапка, ставшая популярной в кругах спортсменов СССР, откуда и берет свое народное название. Хорошо сохраняет тепло помогая не замерзнуть в условиях изменчивой погоды Зоны.",weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=5,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02b.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02b.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=200,width=100,height=100,},},["actors_helmet_02c"]={name="Кепка",description="Обычная кепка с длинным козырьком. В Зоне особенно популярна среди снайперов и солдат особых структур ввиду улучшения видимости при нахождении на открытых пространствах под солнцем.",weight=0.1,durabilityQuality=3.5,durabilityPrice=720,actorsTier=2,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02c.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02c.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=300,width=100,height=100,},},["actors_helmet_02d"]={name="Тактическая панама",description="Головной убор западного образца, используемая оперативниками многих военизированных структур для улучшения видимости в условиях палящего солнца. Хотя последнее в Зоне встретишь нечасто - панама все же встречается в экипировке некоторых сталкеров.",weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02d.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02d.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=400,width=100,height=100,},},["actors_helmet_02e"]={name="Тактическая гарнитура",description="Легкая тактическая гарнитура, призванная облегчить использование средств связи между бойцами того или иного подразделения. В Зоне пользуется спросом среди спецназа военных или иных военизированных структур.",actorsNoPaint=true,weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=1000,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=0,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=1,},model="models/mz/loners/loners_helmet_02e.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02e.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=500,width=100,height=100,},},["actors_helmet_02f"]={name="Армейская кепка",description="Удобная форменная кепка с жестким козырьком, защищающая владельца от ветра, влаги и солнца. Часто встречается в униформе военных подразделений как на Большой Земле, так и на территории Зоны.",weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=0,[DMG_BURN]=1,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02f.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02f.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=600,width=100,height=100,},},["actors_helmet_02g"]={name="Буденовка",description="Форменный зимний головной убор рабоче-крестьянской армии, используемая советскими солдатами вплоть до 1945 года. Суконная основа с ватным подкладом надежно защищает носителя от холода и ветра, а внешний вид позволяет выделится из толпы сталкеров.",weight=0.1,actorsTier=2,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=3,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_02g.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_02g.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={["slotMask"]={[4]=true,[6]=true,[7]=true,},},itemIcon={material=b.ui_icon_armorlist_mz,x=3100,y=700,width=100,height=100,},},["actors_helmet_03a"]={name="Ушанка РККА",description="Раритетная шапка времен Второй мировой войны. Хотя и не представляет никакой действенной защиты для рядового сталкера, может оказаться неплохим украшением, если найдется кто-то, кому важен внешний вид в тяжелых условиях Зоны.",weight=0.45,actorsTier=3,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=8,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_03a.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_03a.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={},itemIcon={material=b.ui_icon_armorlist_mz,x=3200,y=100,width=100,height=100,},},["actors_helmet_03b"]={name="Тактические наушники",description="Активные тактические наушники. Широко используются современными военными организациями по всему земному шару. В условиях Зоны являются очень полезным аксессуаром.",weight=0.45,actorsTier=3,durabilityQuality=3.5,durabilityPrice=1000,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=0,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=2,},model="models/mz/loners/loners_helmet_03b.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_03b.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={},itemIcon={material=b.ui_icon_armorlist_mz,x=3200,y=200,width=100,height=100,},},["actors_helmet_03c"]={name="Советская армейская кепка",description="Советский головной убор, используемый офицерами Красной армии в полевых условиях. Качественные материалы позволяют не бояться ветра, влаги и солнца, а внешний вид позволяет выделиться из толпы сталкеров.",weight=0.45,actorsTier=3,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=1,[DMG_BURN]=4,[DMG_SHOCK]=0,[DMG_ACID]=0,[DMG_RADIATION]=0,[DMG_CLUB]=0,},model="models/mz/loners/loners_helmet_03c.mdl",actorsRenderTable={model="models/mz/loners/loners_helmet_03c.mdl",bone="bip01_head",position=Vector(0.0,-2.2,0),angles=Angle(-15,-90,90),size=1,},actorsBodyGroup={["body"]=1,},actorsBlackListTier={},itemIcon={material=b.ui_icon_armorlist_mz,x=3200,y=300,width=100,height=100,},},["actors_helmet_03d"]={name="Шлемофон",description="Старый советский шлемофон, использующийся в составе обмундирования механизированных войск для защиты владельца от механических повреждений. Не спасет от пули даже мелкого калибра, но защитит от холода и ударов.",actorsNoPaint=true,weight=0.45,actorsTier=3,durabilityQuality=3.5,durabilityPrice=720,protectStats={[DMG_BULLET]=0,[DMG_GENERIC]=4,[DMG_BURN]=8,[DMG_S