local a=ix.plugin.list["inventory-module-aitems-list"]local b=ix.itemsListMaterials function ix.itemsList.weaponsAtt_listregistration(c,d,e)local f=e.attData f["PrintName"]=c.name f["Description"]=f["Description"]or(c.description or"#NoDesc")if f.itemIcon==nil then f.itemIcon={material=b.wick_icon_equipment_seva,x=100,y=2050,width=100,height=50,}end c.itemIcon=f.itemIcon c.noDeathDrop=true c.width=c.itemIcon.width and c.itemIcon.width/50 or 1 c.height=c.itemIcon.height and c.itemIcon.height/50 or 1 c.model=e.model and e.model or(f.Model or"models/props_junk/garbage_bag001a.mdl")function c:OnEntityTakeDamage(g,h)return false end end function ix.itemsList.weaponsAtt_PostListregistration(c,d)ARC9.LoadAtts()end hook.Add("PostPlayerLoadout",a.uniqueID.."PostPlayerLoadout_ARC9_Atts",function(c)if SERVER then ARC9.LoadAtts()net.Start("arc9_reloadatts")net.Send(c)end local d=ents.GetAll()for e,f in ipairs(d)do if IsValid(f)and f:IsWeapon()and f.ARC9 then f:SendWeapon(c)end end ARC9:PlayerSendAttInv(c)end)hook.Add("InventoryItemAdded",a.uniqueID.."InventoryItemAdded",function(c,d,e)if e.attData then local f=d:GetOwner()ARC9:PlayerSendAttInv(f)end end)hook.Add("InventoryItemRemoved",a.uniqueID.."InventoryItemRemoved",function(c,d)if d.attData then local e=c:GetOwner()ARC9:PlayerSendAttInv(e)end end)hook.Add("OnItemTransferred",a.uniqueID.."OnItemTransferred_ATT",function(c,d,e)if c.attData then if d and d.owner then local f=d:GetOwner()if IsValid(f)then ARC9:PlayerSendAttInv(f)end end if e and e.owner then local f=e:GetOwner()if IsValid(f)then ARC9:PlayerSendAttInv(f)end end end end)hook.Add("CharacterVendorTraded",a.uniqueID.."CharacterVendorTraded",function(c,d,e,f)ARC9:PlayerSendAttInv(c)end)hook.Add("InitPostEntity",a.uniqueID.."InitPostEntity_ARC9_Atts",function()timer.Simple(0,function()ix.itemsList.weaponsAtt_PostListregistration()end)end)if IX_PLUGINS_LOADED and ix.itemsList and ix.itemsList.CoreItemsRegistration then ix.itemsList.CoreItemsRegistration()ix.itemsList.weaponsAtt_PostListregistration()end