local a=ix.plugin.list["inventory-module-vendor-dlc-stackrefresh"]a.name="Vendor DLC - StackRefresh"a.author="PURP"a.description=""ix.util.Include("sv_stack-refresh.lua")hook.Add("VendorDLC.NET.ixVendorEdit","VendorDLC.NET.ixVendorEdit.StackRefresh",function(b,c,d)if b=="enableStackRefresh"then d:SetNWBool("enableStackRefresh",c)end end)if CLIENT then hook.Add("VendorDLC.EditorFrame","VendorDLC.EditorFrame.StackRefresh",function(b)b["stackRefresh"]={name="Обновление стаков",draw_editor=function(c,self,d)if not IsValid(d)then return end local e=d:GetNWBool("enableStackRefresh")or false local f=vgui.Create("DCheckBoxLabel",self)f:Dock(TOP)f:DockMargin(5,5,0,10)f:SetFont("ui.Tahoma-8")f:SetText("Включить обновление стаков")f:SetValue(e or false)f:SizeToContents()f.OnChange=function(g,h)c:updateVendor("enableStackRefresh",h)end end,}end)end