local a=ix.plugin.list["mz_spawner"]a.name="Spawn Saver"a.author="Chessnut"a.description="Saves the position of a character."function a:CharacterPreSave(b)local c=b:GetPlayer()if(IsValid(c))then local d,e=c:GetPos(),c:EyeAngles()if(c.ixObsData)then d,e=c.ixObsData[1],c.ixObsData[2]end b:SetData("pos",{d,e,game.GetMap(true)})end end function a:PlayerLoadedCharacter(b,c,d)timer.Simple(0,function()if(IsValid(b))then local e=c:GetData("pos")if(e)then if(e[3]and e[3]:lower()==game.GetMap(true):lower())then b:SetPos(e[1].x and e[1]or b:GetPos())b:SetEyeAngles(e[2].p and e[2]or angle_zero)end c:SetData("pos",nil)end end end)end