function kouka._internal_notify(a)if kouka.utils.IsColor(a[1])==false then table.insert(a,1,kouka.colors.white)end local b={}local c=a[1]for d,e in next,a do local f=type(e)if f=="Entity"then b[#b+1]=kouka.colors.orange b[#b+1]=e:ToString()b[#b+1]=c elseif kouka.utils.IsColor(e)then b[#b+1]=e c=e elseif f=="table"then b[#b+1]=table.ToString(e)else b[#b+1]=tostring(e)end end chat.AddText(kouka.colors.red,"[Kouka] ",unpack(b))end function kouka.Notify(...)kouka._internal_notify({...})end net.Receive("Kouka.Notify",function()kouka._internal_notify(kouka.utils.ReadList())end)